mapper.select:
select cr.*, c.name as certificate_name, c.need_expiry_date as need_expiry_date, c.maxium_file_size as maxium_file_size, c.maxium_file_count as maxium_file_count, c.limit_file_extension as limit_file_extension from certificate_repository cr inner join certificate c on cr.certificate_id = c.id
and cr.target_describe_id = #{request.targetDescribeId}
and c.name like concat( '%', #{request.certificateName}, '%')
and cr.business_type_code = #{request.businessTypeCode}
and cr.certificate_no like concat( '%', #{request.certificateNo}, '%')
and cr.expiry_date between #{request.beginExpiryDate} and #{request.endExpiryDate}
and cr.close_flag = 0
order by cr.gmt_modified desc

select cr.*, c.name as certificate_name, m.name as manufacturer_name, c.need_expiry_date as need_expiry_date, c.maxium_file_size as maxium_file_size, c.maxium_file_count as maxium_file_count, c.limit_file_extension as limit_file_extension from certificate_repository cr inner join certificate c on cr.certificate_id = c.id inner join `mscm-base`.manufacturer m on m.id = cr.manufacturer_id
and cr.target_describe_id = #{request.targetDescribeId}
and c.name like concat( '%', #{request.certificateName}, '%')
and cr.business_type_code = #{request.businessTypeCode}
and cr.certificate_no like concat( '%', #{request.certificateNo}, '%')
and cr.expiry_date between #{request.beginExpiryDate} and #{request.endExpiryDate}
and cr.close_flag = 0
order by cr.gmt_modified desc

select cr.*, c.name as certificate_name, c.need_expiry_date as need_expiry_date, c.maxium_file_size as maxium_file_size, c.maxium_file_count as maxium_file_count, c.limit_file_extension as limit_file_extension from certificate_repository cr inner join certificate c on cr.certificate_id = c.id inner join `mscm-base`.hospital_supplier hs on hs.supplier_id = cr.supplier_id
and hs.hospital_id = #{request.hospitalId}
and cr.business_type_code = #{request.businessTypeCode}
and cr.expiry_date between #{request.beginExpiryDate} and #{request.endExpiryDate}
and cr.expiry_date <= now()
and cr.expiry_date between now() and date_add(now(), interval #{request.expiredMonth} month)
and cr.expiry_date >= date_add(now(), interval 6 month)
and cr.close_flag = 0
order by cr.gmt_modified desc

select cr.*, c.name as certificate_name, c.need_expiry_date as need_expiry_date, c.maxium_file_size as maxium_file_size, c.maxium_file_count as maxium_file_count, c.limit_file_extension as limit_file_extension from certificate_repository cr inner join certificate c on cr.certificate_id = c.id
and cr.supplier_id = #{request.supplierId}
and cr.business_type_code = #{request.businessTypeCode}
and cr.expiry_date between #{request.beginExpiryDate} and #{request.endExpiryDate}
and cr.expiry_date <= now()
and cr.expiry_date between now() and date_add(now(), interval #{request.expiredMonth} month)
and cr.expiry_date >= date_add(now(), interval 6 month)
and cr.close_flag = 0
order by cr.gmt_modified desc

select cr.*, c.name as certificate_name from certificate_repository cr inner join certificate c on cr.certificate_id = c.id
and cr.supplier_id = #{request.supplierId} and cr.target_describe_id = #{request.supplierId}
and cr.close_flag = 0
